        var o = JSSuperTestA2.alloc().init();
        return o.method() == 'A12';
    },
    isPassC: function() {
        var o = JSClassA2.alloc().init();
        return o.method() == 'A12' && JSClassA.clsMethod() == '1';
    }
})

